/**
 * An in-memory cache that holds the values of all the returned queries. This
 * essentialy acts as a small database. It's used to take the results of what
 * could be many different queries (in a cross adapter/connection situation)
 * and join all the results together using the integrator.
 */

/**
 * Module Dependencies
 */

var _ = require('lodash');

/**
 * Cache Module
 *
 * @param {Object} schema
 */

var Cache = module.exports = function (schema) {

  var cache = {};

  // Fill the cache with empty values for each collection
  _.each(_.keys(schema), function(key) {
    cache[key] = [];
  });

  return this;
};
